var getUrl = window.location;
const Base_Url ="https://verasign.se/";


    
function SaveActionTemplate(){
    var id = $(this).attr("data-id");
    var type = $(this).attr("data-type");
    var content = $(this);
    
    var dataSend = {
        "type":type,
        "content" : content,
        "_token":$('meta[name="csrf-token"]').attr('content'),
    }; 

    if(id > 0){
        var lock = $(this).attr("data-lock");
        var hidden = $(this).attr("data-hidden");
        dataSend.id = id;
        dataSend.lock = lock;
        dataSend.hidden = hidden;
    }
    
    $.post({
        "url":Base_Url+"Dashboard/template/save/action",
        data:dataSend,
    }).then((data)=>{
        console.log(data);
    });
    
}

function SaveCacheDateToLayer(id,type,index){
    if(type == "text"){
        $("#body-layer").append(`
            ${TextLayer(id,index)}
        `);
    }else if(type == "shape"){
        $("#body-layer").append(`
            ${ShapeLayer(id,index)}
        `);
    }else if(type == "background"){
        $("#body-layer").append(`
            ${BackGroundLayer(id,index)}
        `);
    }else{

    }
    
}

