function SaveCacheDateToLayer(id, type, index, objectType = null) {
    switch (type) {
        case "text":
            $("#body-layer").append(`${TextLayer(id,index, objectType)}`);
            break;
        case "shape":
            $("#body-layer").append(`${ShapeLayer(id, type, index, objectType)}`);
            break;
        case "list":
            $("#body-layer").append(`${ShapeLayer(id, type, index, objectType)}`);
            break;
        case "background":
            $("#body-layer").append(`${BackGroundLayer(id,index, objectType)}`);
            break;
    }
}

